INSERT INTO `biz_channelarticle` VALUES (1151, '2017-7-17 18:53:32', '2017-7-17 18:53:32', '0007', 1, '<p><span style=\"color: rgb(91, 91, 91); font-family: 宋体; font-size: 19px; letter-spacing: normal; text-indent: 37px; white-space: normal;\">&nbsp; &nbsp;五桥营业所组织安检组、抄表组全员对入户安检工作流程、要求及安全知识进行系统培训学习。</span></p><p><span style=\"color: rgb(91, 91, 91); font-family: 宋体; font-size: 19px; letter-spacing: normal; text-indent: 37px; white-space: normal;\"><img src=\"https://eslink-channel-file.oss-cn-shanghai.aliyuncs.com/253c4bb2-f75e-4549-af13-aa32ed79ff89.jpg\"/></span></p>', '本质安全从基层抓起', '', '本质安全从基层抓起', 'https://eslink-channel-file.oss-cn-shanghai.aliyuncs.com/76893b2b-60b5-42d1-a496-2648c1ae8a66.jpg', '', 21);
