INSERT INTO `biz_channelarticle` VALUES (1153, '2017-7-17 18:55:05', '2017-7-17 18:55:05', '0007', 1, '<p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(91, 91, 91); font-family: 宋体; letter-spacing: normal; white-space: normal; text-indent: 43px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-family: 仿宋_GB2312;font-size:16px\">公司副经理李劼带队参加万州区第八周期人防专业队组训授旗仪式。万州区副区长涂立军、重庆市及万州区人防办等领导出席并授旗。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; color: rgb(91, 91, 91); font-family: 宋体; letter-spacing: normal; white-space: normal; text-indent: 43px; text-align: right;\"><span style=\"margin: 0px; padding: 0px; font-family: 仿宋_GB2312;font-size:16px\">（安全质量部供稿）</span></p><p><img src=\"https://eslink-channel-file.oss-cn-shanghai.aliyuncs.com/b75fde44-0ef4-4b07-ae4b-14ba3a0eea17.jpg\"/></p>', '我司应急抢险队接受区人防专业队组训授旗', '', '我司应急抢险队接受区人防专业队组训授旗', 'https://eslink-channel-file.oss-cn-shanghai.aliyuncs.com/99ab3226-40aa-47fa-8b3f-2c9f0cd393af.jpg', '', 21);
