INSERT INTO `biz_channelarticle` VALUES (1166, '2017-7-17 19:16:43', '2017-7-17 19:16:43', '0007', 1, '<p><span style=\"color: rgb(91, 91, 91); font-family: 仿宋_GB2312; letter-spacing: normal; text-align: center; white-space: normal;font-size:16px\">3月6日，陕西省延安市燃气总公司贺总一行来司交流学习</span></p><p><span style=\"color: rgb(91, 91, 91); font-family: 仿宋_GB2312; letter-spacing: normal; text-align: center; white-space: normal;font-size:16px\"><img src=\"https://eslink-channel-file.oss-cn-shanghai.aliyuncs.com/7d1885ec-d917-4154-b534-be64a3c4a9fe.jpg\"/></span></p>', '陕西省延安市天然气总公司来司交流学习', '', '陕西省延安市天然气总公司来司交流学习', 'https://eslink-channel-file.oss-cn-shanghai.aliyuncs.com/b5854894-f5ad-41b9-aec2-9a6704a6acc8.jpg', '', 21);
